mapper.select:
SELECT IFNULL(MAX(ctgry_id), CONCAT(#{parentId}, '00')) 
FROM category
WHERE ctgry_up_id = #{parentId}

mapper.insert:
INSERT INTO category (
    ctgry_id, adm_cd, ctgry_nm, ctgry_up_id,
    ctgry_reg_ymdt, ctgry_adm_cd
) VALUES (
    #{ctgryId}, #{admCd}, #{ctgryNm}, #{ctgryUpId},
    NOW(), #{ctgryAdmCd}
)
