mapper.insert:
/* 상품 삽입 */
SELECT CASE WHEN COUNT(*) = 0 THEN 'PD_C001' ELSE CONCAT( 'PD_C', MAX(CAST(SUBSTRING_INDEX(gds_cd, 'PD_C', -1) AS UNSIGNED)) + 1 ) END AS newCode FROM goods WHERE gds_cd LIKE 'PD_C%';
INSERT INTO goods ( gds_cd, gds_nm, gds_amt, gds_type, gds_period_quantity, gds_unit, gds_reg_adm_cd, gds_reg_ymdt, gds_status, gds_target_member_type ) VALUES ( #{gdsCd}, #{gdsNm}, #{gdsAmt}, #{gdsType}, #{gdsPeriodQuantity}, #{gdsUnit}, #{gdsRegAdmCd}, #{gdsRegYmdt}, #{gdsStatus}, #{gdsTargetMemberType} )

mapper.select:
/* 상품 조회 (동적 쿼리) */ SELECT gds_cd AS gdsCd, gds_nm AS gdsNm, gds_amt AS gdsAmt, gds_type AS gdsType, gds_period_quantity AS gdsPeriodQuantity, gds_unit AS gdsUnit, gds_reg_adm_cd AS gdsRegAdmCd, gds_reg_ymdt AS gdsRegYmdt, gds_mdfcn_adm_cd AS gdsMdfcnAdmCd, gds_mdfcn_ymdt AS gdsMdfcnYmdt, gds_status AS gdsStatus FROM goods
AND gds_nm LIKE CONCAT('%', #{searchKeyword}, '%')
AND gds_cd LIKE CONCAT('%', #{searchKeyword}, '%')
AND gds_reg_adm_cd LIKE CONCAT('%', #{searchKeyword}, '%')
AND DATE(gds_reg_ymdt) >= #{startDate}
AND DATE(gds_reg_ymdt) <= #{endDate}
AND gds_type = #{gdsType}
AND gds_target_member_type = #{gdsTargetMemberType}
AND gds_status = #{gdsStatus}
ORDER BY gds_cd ASC
