mapper.select:
SELECT COUNT(*) > 0
FROM member
WHERE mbr_nicknm = #{memberNickname}
  AND mbr_cd != #{memberCode}

SELECT COUNT(*) > 0
FROM member
WHERE mbr_eml = #{memberEmail}
  AND mbr_cd != #{memberCode}

SELECT COUNT(*) > 0
FROM member
WHERE mbr_telno = #{memberTelNo}
  AND mbr_cd != #{memberCode}
